#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="E:\Learn\for-js\node_modules\.pnpm\ts-node@10.9.1_2abc08acbb4fe741ae8218df9aa74f5c\node_modules\ts-node\dist\node_modules;E:\Learn\for-js\node_modules\.pnpm\ts-node@10.9.1_2abc08acbb4fe741ae8218df9aa74f5c\node_modules\ts-node\node_modules;E:\Learn\for-js\node_modules\.pnpm\ts-node@10.9.1_2abc08acbb4fe741ae8218df9aa74f5c\node_modules;E:\Learn\for-js\node_modules\.pnpm\node_modules;E:\Learn\for-js\node_modules;E:\Learn\node_modules;E:\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/e/Learn/for-js/node_modules/.pnpm/ts-node@10.9.1_2abc08acbb4fe741ae8218df9aa74f5c/node_modules/ts-node/dist/node_modules:/mnt/e/Learn/for-js/node_modules/.pnpm/ts-node@10.9.1_2abc08acbb4fe741ae8218df9aa74f5c/node_modules/ts-node/node_modules:/mnt/e/Learn/for-js/node_modules/.pnpm/ts-node@10.9.1_2abc08acbb4fe741ae8218df9aa74f5c/node_modules:/mnt/e/Learn/for-js/node_modules/.pnpm/node_modules:/mnt/e/Learn/for-js/node_modules:/mnt/e/Learn/node_modules:/mnt/e/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$env_node_path$pathsep$new_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../dist/bin-cwd.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../dist/bin-cwd.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../dist/bin-cwd.js" $args
  } else {
    & "node$exe"  "$basedir/../../dist/bin-cwd.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
